import {
  REGISTER,
  LOGIN,
  LOGIN_TEXT,
  REGISTER_TEXT,
} from './../../constants/app.constants';
import { UserService } from './services/user.service';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss'],
})
export class RegisterComponent implements OnInit {
  show = true;
  login = LOGIN;
  registerUser = REGISTER;
  login_text = LOGIN_TEXT;
  register_text = REGISTER_TEXT;

  constructor(private userService: UserService) {}
  ngOnInit(): void {
    this.userService.getUsers$.subscribe((x) => {
      console.log(x);
    });
  }

  hideShow() {
    console.log(this.show);

    this.show = !this.show;
  }

  register(type: string) {
    this.login = type;
  }
}
<div class=" h-screen">
  <div class=" bg-slate-100 ">
    <div class="px-60 flex justify-between p-10">

      <div>
        <h1 class="text-2xl text-blue-900 my-4 ml-9 ">Welcome to <span class="fw-bold text-5xl">Matcher</span>
        </h1>
        <h3 class="text-blue-600 text-2xl my-3 ml-9">Let's connect !</h3>

        <div class="shadow-md bg-white mx-10 py-20 border-slate-300 rounded-md  text-slate-500">
          <h1 class="font-semibold text-center my-3">
            {{login === login ? login_text: register_text }}
          </h1>
          <div class="text-center py-3">
            <button class="border border-r-0 border-slate-100 rounded-l-md  p-2 "
              [ngClass]="{'bg-blue-300 text-white': login=== login}" (click)="register(login)">
              Sign In
            </button>
            <button class="bg-white border text-white border-l-0 rounded-r-md border-blue-50 p-2"
              [ngClass]="{'bg-blue-300 text-white': login=== login}" (click)="register(registerUser)">
              Create Account
            </button>
          </div>
          <div class="px-12">
            <div class="mt-3">
              <input type="text" class="border rounded-md py-4 px-3 w-full" placeholder="Username">
            </div>
            <div class="mt-3">
              <div class="relative">
                <input type="{{ show ? 'password' : 'text' }}" class="border rounded-md py-4 px-3 w-full relative"
                  placeholder="Password" />
                <div class="absolute flex items-center right-3 cursor-pointer top-0 bottom-0" (click)="hideShow()">
                  <div [style.visibility]="show ? 'visible' : 'hidden'">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor"
                      class="bi bi-eye-fill" viewBox="0 0 16 16">
                      <path d="M10.5 8a2.5 2.5 0 1 1-5 0 2.5 2.5 0 0 1 5 0z" />
                      <path
                        d="M0 8s3-5.5 8-5.5S16 8 16 8s-3 5.5-8 5.5S0 8 0 8zm8 3.5a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7z" />
                    </svg>
                  </div>
                  <div [style.visibility]="show ? 'hidden' : 'visible'">
                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor"
                      class="bi bi-eye-slash-fill" viewBox="0 0 16 16">
                      <path
                        d="m10.79 12.912-1.614-1.615a3.5 3.5 0 0 1-4.474-4.474l-2.06-2.06C.938 6.278 0 8 0 8s3 5.5 8 5.5a7.029 7.029 0 0 0 2.79-.588zM5.21 3.088A7.028 7.028 0 0 1 8 2.5c5 0 8 5.5 8 5.5s-.939 1.721-2.641 3.238l-2.062-2.062a3.5 3.5 0 0 0-4.474-4.474L5.21 3.089z" />
                      <path
                        d="M5.525 7.646a2.5 2.5 0 0 0 2.829 2.829l-2.83-2.829zm4.95.708-2.829-2.83a2.5 2.5 0 0 1 2.829 2.829zm3.171 6-12-12 .708-.708 12 12-.708.708z" />
                    </svg>
                  </div>
                </div>
              </div>



            </div>
          </div>
        </div>
      </div>
      <img src="../../../assets/images/register-bg.svg" class="w-1/2 aspect-video md:aspect-square">
    </div>



  </div>

</div>
