import {
  LOGIN,
  LOGIN_BTN_TEXT,
  LOGIN_TEXT,
  REGISTER,
  REGISTER_BTN_TEXT,
  REGISTER_TEXT,
} from 'src/app/constants/app.constants';
import { Component, OnInit } from '@angular/core';
import { UserService } from '../services/user.service';
import { ActivatedRoute, Router } from '@angular/router';
import { FormBuilder, FormGroup } from '@angular/forms';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.scss'],
})
export class RegisterComponent implements OnInit {
  show = true;
  type = LOGIN;
  loginType = LOGIN;
  registerType = REGISTER;
  login_text = LOGIN_TEXT;
  register_text = REGISTER_TEXT;
  loginBtnText = LOGIN_BTN_TEXT;
  registerBtnText = REGISTER_BTN_TEXT;

  login!: FormGroup;
  register!: FormGroup;

  constructor(
    private userService: UserService,
    private route: ActivatedRoute,
    private router: Router,
    private fb: FormBuilder
  ) {}

  ngOnInit(): void {
    this.getParams();
    this.createLoginForm();
    this.getUser();
  }

  loginHandler(): void {
    console.log(this.login.value);
  }

  hideShow(): void {
    this.show = !this.show;
  }

  changeType(type: string): void {
    this.router.navigate(['account'], { queryParams: { type: type } });
  }

  private getParams(): void {
    this.route.queryParams.subscribe((type) => {
      this.type = type['type'];
      console.log(
        !(this.type === this.loginType || this.type === this.registerType)
      );

      if (!(this.type === this.loginType || this.type === this.registerType))
        this.type = this.loginType;
    });
  }

  private createLoginForm(): void {
    this.login = this.fb.group({
      username: [],
      password: [],
    });
  }

  private getUser(): void {
    this.userService.getUsers$.subscribe((x) => {
      console.log(x);
    });
  }
}
<div class="h-screen">
  <div class="bg-slate-100 ">
    <div class="px-60 flex justify-between p-10">

      <div class="h-screen">
        <h1 class="text-2xl text-blue-900 my-4 ml-9 ">Welcome to <span
            class="fw-bold text-5xl text-red-600">Matcher</span>
        </h1>
        <h3 class="text-blue-600 text-2xl my-3 ml-9">Let's connect !</h3>

        <div class="shadow-md bg-white mx-10 py-10 border-slate-300 rounded-md  text-slate-500">
          <h1 class="font-semibold text-center py-5">
            {{type === loginType ? login_text: register_text }}
          </h1>

          <div class="px-12" *ngIf="type===loginType">
            <form (ngSubmit)="loginHandler()">

              <div class="mt-3">
                <input type="text" class="text-input" placeholder="Username">
              </div>
              <div class="mt-3">
                <div class="relative">
                  <input type="{{ show ? 'password' : 'text' }}"
                    class="border rounded-md py-3 px-3 w-full relative focus:outline-none focus:border-sky-500 focus:ring-sky-500 focus:ring-1"
                    placeholder="Password" />
                  <div class="absolute flex items-center right-3 cursor-pointer top-0 bottom-0" (click)="hideShow()">
                    <div [style.visibility]="show ? 'visible' : 'hidden'">
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor"
                        class="bi bi-eye-fill" viewBox="0 0 16 16">
                        <path d="M10.5 8a2.5 2.5 0 1 1-5 0 2.5 2.5 0 0 1 5 0z" />
                        <path
                          d="M0 8s3-5.5 8-5.5S16 8 16 8s-3 5.5-8 5.5S0 8 0 8zm8 3.5a3.5 3.5 0 1 0 0-7 3.5 3.5 0 0 0 0 7z" />
                      </svg>
                    </div>
                    <div [style.visibility]="show ? 'hidden' : 'visible'">
                      <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor"
                        class="bi bi-eye-slash-fill" viewBox="0 0 16 16">
                        <path
                          d="m10.79 12.912-1.614-1.615a3.5 3.5 0 0 1-4.474-4.474l-2.06-2.06C.938 6.278 0 8 0 8s3 5.5 8 5.5a7.029 7.029 0 0 0 2.79-.588zM5.21 3.088A7.028 7.028 0 0 1 8 2.5c5 0 8 5.5 8 5.5s-.939 1.721-2.641 3.238l-2.062-2.062a3.5 3.5 0 0 0-4.474-4.474L5.21 3.089z" />
                        <path
                          d="M5.525 7.646a2.5 2.5 0 0 0 2.829 2.829l-2.83-2.829zm4.95.708-2.829-2.83a2.5 2.5 0 0 1 2.829 2.829zm3.171 6-12-12 .708-.708 12 12-.708.708z" />
                      </svg>
                    </div>
                  </div>
                </div>

                <div class="text-center py-5">
                  <button type="submit" class="btn">
                    {{ loginBtnText}}
                  </button>

                </div>

                <p class="text-sm text-center">Don't have account ? <span
                    class="cursor-pointer text-blue-400 font-semibold"
                    (click)="changeType(registerType)">Register</span>
                </p>
              </div>
            </form>

          </div>
          <div class="px-12" *ngIf="type===registerType">
            <div class="mt-3">
              <input type="text" class="text-input" placeholder="Username">
            </div>
            <div class="mt-3">
              <form class="relative">
                <input type="{{ show ? 'password' : 'text' }}" class="password-input" placeholder="Password" />
                <div class="absolute flex items-center right-2 justify-between cursor-pointer top-0 bottom-0"
                  (click)="hideShow()">
                  <div [style.visibility]="show ? 'visible' : 'hidden'">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5"
                      stroke="currentColor" class="w-5 h-5">
                      <path stroke-linecap="round" stroke-linejoin="round"
                        d="M2.036 12.322a1.012 1.012 0 010-.639C3.423 7.51 7.36 4.5 12 4.5c4.638 0 8.573 3.007 9.963 7.178.07.207.07.431 0 .639C20.577 16.49 16.64 19.5 12 19.5c-4.638 0-8.573-3.007-9.963-7.178z" />
                      <path stroke-linecap="round" stroke-linejoin="round" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                    </svg>

                  </div>
                  <div [style.visibility]="show ? 'hidden' : 'visible'">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5"
                      stroke="currentColor" class="w-5 h-5">
                      <path stroke-linecap="round" stroke-linejoin="round"
                        d="M3.98 8.223A10.477 10.477 0 001.934 12C3.226 16.338 7.244 19.5 12 19.5c.993 0 1.953-.138 2.863-.395M6.228 6.228A10.45 10.45 0 0112 4.5c4.756 0 8.773 3.162 10.065 7.498a10.523 10.523 0 01-4.293 5.774M6.228 6.228L3 3m3.228 3.228l3.65 3.65m7.894 7.894L21 21m-3.228-3.228l-3.65-3.65m0 0a3 3 0 10-4.243-4.243m4.242 4.242L9.88 9.88" />
                    </svg>

                  </div>
                </div>
              </form>
              <form class="relative mt-3">
                <input type="{{ show ? 'password' : 'text' }}" class="password-input" placeholder="Confirm password" />
                <div class="absolute flex items-center justify-between right-2 cursor-pointer top-0 bottom-0"
                  (click)="hideShow()">
                  <div [style.visibility]="show ? 'visible' : 'hidden'">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5"
                      stroke="currentColor" class="w-5 h-5">
                      <path stroke-linecap="round" stroke-linejoin="round"
                        d="M2.036 12.322a1.012 1.012 0 010-.639C3.423 7.51 7.36 4.5 12 4.5c4.638 0 8.573 3.007 9.963 7.178.07.207.07.431 0 .639C20.577 16.49 16.64 19.5 12 19.5c-4.638 0-8.573-3.007-9.963-7.178z" />
                      <path stroke-linecap="round" stroke-linejoin="round" d="M15 12a3 3 0 11-6 0 3 3 0 016 0z" />
                    </svg>

                  </div>
                  <div [style.visibility]="show ? 'hidden' : 'visible'">
                    <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5"
                      stroke="currentColor" class="w-5 h-5">
                      <path stroke-linecap="round" stroke-linejoin="round"
                        d="M3.98 8.223A10.477 10.477 0 001.934 12C3.226 16.338 7.244 19.5 12 19.5c.993 0 1.953-.138 2.863-.395M6.228 6.228A10.45 10.45 0 0112 4.5c4.756 0 8.773 3.162 10.065 7.498a10.523 10.523 0 01-4.293 5.774M6.228 6.228L3 3m3.228 3.228l3.65 3.65m7.894 7.894L21 21m-3.228-3.228l-3.65-3.65m0 0a3 3 0 10-4.243-4.243m4.242 4.242L9.88 9.88" />
                    </svg>

                  </div>
                </div>
              </form>

              <div class="text-center py-5">
                <button type="button" type="button" class="btn" (click)="loginHandler()">
                  {{ registerBtnText}}
                </button>

              </div>

              <p class="text-sm text-center">Already have account? <span
                  class="cursor-pointer text-blue-400 font-semibold" (click)="changeType(loginType)">Login</span>
              </p>
            </div>
          </div>
        </div>
      </div>
      <img src="../../../assets/images/register-bg.svg" class="w-1/2 aspect-video md:aspect-square">
    </div>



  </div>

</div>
