import { Observable } from 'rxjs';
import { User } from 'src/app/components/account/models/user.interface';
import { Component, OnInit } from '@angular/core';
import { AccountService } from 'src/app/services/account.service';

@Component({
  selector: 'app-menu',
  templateUrl: './menu.component.html',
  styleUrls: ['./menu.component.scss'],
})
export class MenuComponent implements OnInit {
  loggedIn!: boolean;
  currentUser$!: Observable<User | unknown>;
  constructor(private accountService: AccountService) {}

  ngOnInit(): void {
    this.setCurrentUser();
  }

  private setCurrentUser() {
    const user: User = JSON.parse(localStorage.getItem('user')!);
    this.accountService.setCurrentUser(user);
    this.currentUser$ = this.accountService.currentUser$;
  }

  logout() {
    this.accountService.logout();
    this.loggedIn = false;
  }
}
<div class="bg-white flex px-20 py-4 border-b border-gray-100  justify-between items-center">
  <div [routerLink]="['/']">
    <h1 class="text-blue-700 cursor-pointer  font-sans">Match</h1>
  </div>
  <div>
    <ul class="flex space-x-8 font-body font-bold  title">
      <li class="text-blue-600 cursor-pointer" [routerLink]="['/']">Home</li>
      <li class="text-blue-400 cursor-pointer">About us</li>
      <li class="text-blue-400 cursor-pointer">Features</li>
      <li class="text-blue-400 cursor-pointer">Contact Us</li>
    </ul>
  </div>
  <div>
    <button [routerLink]="['/account']"
      class="border-3 border-red-500 shadow-sm shadow-slate-600 text-sm flex justify-between items-center font-bold text-white bg-red-500 py-2 px-5 uppercase rounded-full hover:bg-blue-500 hover:border-blue-500  hover:text-white transition duration-75">
      <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke-width="1.5" stroke="currentColor"
        class="stroke-2 w-5 h-5">
        <path stroke-linecap="round" stroke-linejoin="round"
          d="M15.75 9V5.25A2.25 2.25 0 0013.5 3h-6a2.25 2.25 0 00-2.25 2.25v13.5A2.25 2.25 0 007.5 21h6a2.25 2.25 0 002.25-2.25V15m3 0l3-3m0 0l-3-3m3 3H9" />
      </svg>
      <span class="ml-3">
        Log in
      </span>
    </button>
    <button [routerLink]="['/account']"
      class="border-3 border-red-500 shadow-sm shadow-slate-600 text-sm flex justify-between items-center font-bold text-white bg-red-500 py-2 px-5 uppercase rounded-full hover:bg-blue-500 hover:border-blue-500  hover:text-white transition duration-75">
      <span>{{currentUser$ | async | json}}</span>
    </button>
  </div>
</div>
